    const downloadImageAsBase64 = async (url) => {
        try {
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Failed to download the image.');
            }
            const blob = await response.blob();
            const reader = new FileReader();
            return new Promise((resolve, reject) => {
                reader.onloadend = () => {
                const dataURI = reader.result;
                resolve(dataURI);
                };
                reader.onerror = () => {
                reject(new Error('Failed to convert the image to base64.'));
                };
                reader.readAsDataURL(blob);
            });
        } catch (error) {
            console.error(error);
            throw error;
        }
    };

    const bufferImage = async (url) => {
        console.log(url)
        try {
          const imageBuffer = await downloadImageAsBase64(url);
          const base64Data = imageBuffer.replace("data:image/jpeg;base64,", "").replace("data:image/png;base64,", "");
          return new ImageRun({
            data: Uint8Array.from(atob(base64Data), (c) => c.charCodeAt(0)),
            transformation: {
              height: 300,
              width: 300,
            },
          });
        } catch (error) {
          console.error(error);
        }
    };
       
    const addImage = async (data, docHeader, firstSection) => {
        try {
            const images = await Promise.all(data.map(async (item) => ({
                jenisBencana: item.jenisBencana,
                RegImages: await Promise.all(item.RegImages.map(async (image) => ({
                    path: await bufferImage(`http://${process.env.REACT_APP_HOST}:5000/` + image.path.replace(/\\/g, "/")),
                }))),
            })));
            // Add the remaining content
            let element = []
            for (let i = 0; i < images.length; i++) {
                for(let j = 0; j < images[i]?.RegImages.length; j++)
                {
                    if(j%2 === 0){
                        element.push(new Paragraph({text: '', pageBreakBefore: true}))
                        element.push(docHeader);
                        element.push(new Paragraph(''))
                        element.push(
                            new Paragraph({
                                alignment: 'center',
                                children: [
                                    new TextRun({
                                        font: "Arial",
                                        text: "DOKUMENTASI KEJADIAN "+images[i].jenisBencana.toString().toUpperCase(),
                                        size: 28,
                                        bold: true
                                    })
                                ]
                            }),   
                        )
                        element.push(new Paragraph(''))
                    }
                    element.push(
                        new Paragraph({
                            alignment: 'center',
                            children: [images[i]?.RegImages[j].path]
                        })
                    )
                    element.push(new Paragraph(''))
                }
            }

            const combinedDocument = new Document({
                defaultFontName: 'Arial',
                sections: [
                {
                    properties: {},
                    children: [...firstSection, ...element],
                },
                ],
            });
            Packer.toBlob(combinedDocument).then(blob => {
                saveAs(blob, "Nota Dinas.docx");
            });  
        } catch (error) {
            console.error(error);
        }
    };
    
